SELECT INF.REST_ID, INF.REST_NAME, INF.FOOD_TYPE, INF.FAVORITES, INF.ADDRESS, REV.SCORE FROM REST_INFO INF, (SELECT REST_ID, ROUND(AVG(REVIEW_SCORE),2) AS SCORE FROM REST_REVIEW GROUP BY REST_ID) REV WHERE REV.REST_ID = INF.REST_ID AND INF.ADDRESS LIKE "서울%" ORDER BY REV.SCORE DESC, INF.FAVORITES DESC;